document.body.innerHTML = `


<button onclick="add_User()" class="addButton btn-secondary">AddNewUser</button>

<div class="add-user-form add">

    <input class="add-user_name"  placeholder="Enter User Name" />
    <input class="add-userName" placeholder="Enter userName" />
    <input class="add-user_avatar" placeholder="Enter User Avatar" />
    <input class="add-user_fork" placeholder="Enter User Fork" />
    <input class="add-user_star" placeholder="Enter User Star" />
  
    <button  onclick="addUser()" class="btn btn-success add">Add</button>
</div>
    

    <section class="user-list"></section>`; 
 
 async function getAllUsers(){
     const data = await fetch("https://61f77d2639431d0017eaf8ab.mockapi.io/Users",{method: 'GET'});
    const users = await data.json();
    
    const userContainer = document.querySelector(".user-list");

    userContainer.innerHTML="";
   setTimeout(()=>{
    users.forEach((user) => {

       
        userContainer.innerHTML += `
            <div class="user-container">
                <img class="user-avatar" src="${user.avatar}" alt=${user.name}/>    
                <p class="user-name">${user.name}</p>
                
                <button type="button" onclick="gituser(${user.id})" class="btn btn-outline-warning">${user.userName} </button>
                <div>
                
                
                <button type="button" class="btn btn-outline-info"><b>fork</b> ${user.fork}</button>
                <button type="button" class="btn btn-outline-info"><b>star</b> ${user.star}</button>
                </div>
                <div class="access_button">
                

                <button  onclick="repo(${user.id})" class=" repo btn btn-dark">Repo</button>
                <button  class="edit btn btn-dark" onclick="edit_User(${user.id})">Edit</button>
                <button  class="delete btn btn-dark" onclick="deleteUser(${user.id})">Delete</button>
                
                <div class="edit-user-form edit-${user.id}">
                <input value="${user.name}" class="edit-${user.id}-user-name" placeholder="Enter user name" />
                <input value="${user.userName}" class="edit-${user.id}-user-userName" placeholder="Enter user-name" />
                <input value="${user.avatar}" class="edit-${user.id}-user-avatar" placeholder="Enter user Avatar" />
                <input value="${user.fork}" class="edit-${user.id}-user-fork" placeholder="Enter user Fork" />
                <input value="${user.star}" class="edit-${user.id}-user-star" placeholder="Enter user Star" />
                <button onclick="saveUser(${user.id})">Save</button>
                </div>
                
                
                <div class="getRepo repo-${user.id}">
                         
                    <li><a href="url" class="repo-${user.id}-user-repo">${user.userName}/${user.batch}/${user.repo1}</a></li>
                    <li><a href="url" class="repo-${user.id}-user-repo">${user.userName}/${user.batch}/${user.repo2}</a></li>
                    <li><a href="url" class="repo-${user.id}-user-repo">${user.userName}/${user.batch}/${user.repo3}</a></li>
                    <li><a href="url" class="repo-${user.id}-user-repo">${user.userName}/${user.batch}/${user.repo4}</a></li>
                    <li><a href="url" class="repo-${user.id}-user-repo">${user.userName}/${user.batch}/${user.repo5}</a></li>
                    
                </div>
                
 
                </div>

            </div>

        `;
    });

}, 100);   
}


getAllUsers();


async function addUser(){
    console.log("Adding...")
    const name = document.querySelector('.user_name').value;
    const userName = document.querySelector('.userName').value;
    const avatar = document.querySelector('.user_avatar').value;
    const fork = document.querySelector('.user_fork').value;
    const star = document.querySelector('.user_star').value;
    // console.log(userName, userAvatar)
    const data = await fetch("https://61f77d2639431d0017eaf8ab.mockapi.io/Users",
        {method: "POST",
        headers:{"Content-Type": "application/json"},
        body:JSON.stringify({ name: name, userName: userName, avatar: avatar, fork: fork, star: star })
    });

    getAllUsers();
}

async function deleteUser(userId){
    console.log("Deleting...", userId)
    const data = await fetch("https://61f77d2639431d0017eaf8ab.mockapi.io/Users/" + userId,
        {method: 'DELETE'});

        getAllUsers();
}

async function edit_User(userId){
    // console.log("Edit")
    const editUserForm = document.querySelector(`.edit-${userId}`);
    editUserForm.style.display = editUserForm.style.display === "block" ? "none" : "block";
  
}
async function add_User(){
    const addUserForm = document.querySelector('.add');
    addUserForm.style.display = addUserForm.style.display === "block" ? "none" : "block";
}

async function repo(userId){
    const repoUserForm = document.querySelector(`.repo-${userId}`);
    repoUserForm.style.display = repoUserForm.style.display === "block" ? "none" : "block";
}

async function saveUser(userId){
    console.log("save");
    const userName = document.querySelector(`.edit-${userId}-user-name`).value;
    const user_Name = document.querySelector(`.edit-${userId}-user-userName`).value;
    const userAvatar = document.querySelector(`.edit-${userId}-user-avatar`).value;
    const userFork = document.querySelector(`.edit-${userId}-user-fork`).value;
    const userStar = document.querySelector(`.edit-${userId}-user-star`).value;

    // console.log(name, avatar)
    const data = await fetch("https://61f77d2639431d0017eaf8ab.mockapi.io/Users/" + userId,
    {
    method: "PUT",
    headers:{"Content-Type": "application/json"},
    body: JSON.stringify({ name: userName, userName: user_Name,  avatar: userAvatar, fork: userFork, star: userStar }),
});
getAllUsers();
}




 
